package com.bit.hello.v;

public class Value01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int intNum1 = 0;
		int intSum = 0;

		intNum1++; // intNum1 = 1
		intSum += intNum1; // intSum += 1

		intNum1++; // intNum1 = 2
		intSum += intNum1; // intSum += 2

		intNum1++; // intNum1 = 3
		intSum += intNum1; // intSum += 3

		intNum1++; // intNum1 = 4
		intSum += intNum1; // intSum += 4

		intNum1++; // intNum1 = 5
		intSum += intNum1; // intSum += 5

		intNum1++; // intNum1 = 6
		intSum += intNum1; // intSum += 6
		
		System.out.println(intSum);

	}

}
